identifiers &lt;span style=&#34;color:#ff79c6&#34;&gt;=&lt;/span&gt; [&lt;span style=&#34;color:#f1fa8c&#34;&gt;&amp;#34;arn:aws:iam::${data.aws_caller_identity.current.account_id}:user/jdheyburn&amp;#34;&lt;/span&gt;]
}
resources &lt;span style=&#34;color:#ff79c6&#34;&gt;=&lt;/span&gt; [&lt;span style=&#34;color:#f1fa8c&#34;&gt;&amp;#34;*&amp;#34;&lt;/span&gt;]
}
&lt;span style=&#34;color:#ff79c6&#34;&gt;statement&lt;/span&gt; {
