icon = &lt;span style=&#34;color:#f1fa8c&#34;&gt;&amp;#34;fas fa-rss&amp;#34;&lt;/span&gt;
weight = &lt;span style=&#34;color:#bd93f9&#34;&gt;6&lt;/span&gt;
