rel = &lt;span style=&#34;color:#f1fa8c&#34;&gt;&amp;#34;alternate&amp;#34;&lt;/span&gt;
type = &lt;span style=&#34;color:#f1fa8c&#34;&gt;&amp;#34;application/rss+xml&amp;#34;&lt;/span&gt;
